$.fn.mpUpdate = function(phpLoc, callback, onSuccess){
	var base_url = "http://scifi.openfilmpod.com/admin/cp/";
	var thisObj = $(this);
	var submitObj = $(thisObj).find('#submit');
	$(this).validate({
		submitHandler: function(form){
			$(submitObj).attr('disabled', true);
			$(form).find('.placeholder').each(function(){
				var input = $(this);
				if(input.val() == input.attr('placeholder')) input.val('');
				input.removeClass('placeholder');
			});
			
			var passedData = $(form).serialize();
			if($(form).find('#catchild').length > 0){
				var catChildId = parseInt($(form).find('#catchild option').filter(':selected').attr('id'));
				passedData += '&catchildid=' + catChildId;
			}
			
			if($(form).find('#slidertype').length > 0){
				var sliderId = parseInt($(form).find('#slidertype option').filter(':selected').attr('id'));
				passedData += '&slidertypeid=' + sliderId;
			}
			
			$.post(base_url + phpLoc,	
				passedData,
				function(data){
					if(data.redirect) window.location = "http://scifi.openfilmpod.com/admin/cp/logout/";
					if(callback){
						if(onSuccess){
							if(!data.hasError) callback($(thisObj), data);
						}else callback($(thisObj), data);
					} 
					var result = $(thisObj).find('#result');
					if(data.hasError) $(result).hide().empty().append('<p class="error">' + data.message + '</p>');
					else $(result).hide().empty().append('<p class="success">' + data.message + '</p>');
					$(result).fadeTo(500, 1, function(){
						$(submitObj).attr('disabled', false);
						setTimeout(function(){
							$(result).fadeTo(500, 0);
						}, 6000);
					});
				},
				"json");
		}
	});
}

