$.fn.mpNameClick = function(url, formType, callback, onSuccess){
	$(this).click(function(){
		var base_url = "http://scifi.openfilmpod.com/admin/cp/";
		var thisObj = $(this);
		var comList = $(this).parent().parent().parent();
		var classObj = $(this).parent();
		var nameText = $(this).html().split('"').join('&#34;');
		var idText = $(classObj).find('.id').html();
		var positionText = $(classObj).find('.position').html();
		if($(classObj).find('.time').length > 0) var timeText = $(classObj).find('.time').html();
		$(classObj).fadeTo(250, 0, function(){
			var newForm;
			switch(formType){
				case "comments":
					newForm = $('<div class="id">' + idText + '</div><form><input type="text" class="hidden" id="token" name="token" value="' + uToken + '" /><fieldset><input type="text" placeholder="Enter the admins name here." name="objname" id="objname" value="' + nameText + '"/><input type="submit" id="submit" value="Update" name="submit" /></fieldset></form>');
					break;
				case "tabs":
					newForm = $('<div class="id">' + idText + '</div><div class="handle"><span></span></div><div class="position">' + positionText + '</div><form><input type="text" class="hidden" id="token" name="token" value="' + uToken + '" /><fieldset><input type="text" placeholder="Enter the admins name here." name="objname" id="objname" value="' + nameText + '"/><input type="submit" id="submit" value="Update" name="submit" /></fieldset></form>');
					break;
				case "midrolls":
					newForm = $('<div id="update-time"><input type="text" placeholder="Enter the midroll time (in seconds) here." name="objname" id="objname" value="' + timeText + '"/><input type="submit" id="submit" value="Update" name="submit" /></div>');
					break;
			}
			
			$(classObj).empty().append($(newForm)).fadeTo(500, 1);
			$(newForm).find('#submit').click(function(){
				var submitData;
				if(formType !== "midrolls") submitData = $(newForm).serialize() + '&objid=' + idText;
				else submitData = 'objTime=' + $(newForm).find('input').val();
				$.post(base_url + url,
				submitData,
				function(data){
					if(data.redirect) window.location = "http://scifi.openfilmpod.com/admin/cp/logout/";
					if(formType == "midrolls"){
						sendCallback();
					}else{
						var resultObj = $(comList).find('#result');
						if(data.hasError){
							$(resultObj).hide().empty().append('<p class="error">' + data.message + '</p>');
							if(!onSuccess && callback) sendCallback();	
						}
						else{
							if(callback) sendCallback();
							$(resultObj).hide().empty().append('<p class="success">' + data.message + '</p>');
							$(resultObj).fadeTo(500, 1, function(){
								setTimeout(function(){
									$(resultObj).fadeTo(500, 0);
								}, 6000);
							});
						}
					}
					
					function sendCallback(){
						callback($(thisObj), $(classObj), data);
					}
				},
				"json");
				return false;
			});
		});
	});
}

