$.fn.mpScroll = function(settings){
	settings = $.extend({
		speed: 0.07,
		stripOffset: 50,
		headingCutoff: 16
	}, settings);
	
	return this.each(function(){
		var travelWidth = 0,
		initStrip = $(this),
		itemWidth = 280,
		itemMargin = 4,
		itemheight = 110,
		sliderItems = $(initStrip).find('#slider-items'),
		numItems = $(initStrip).find('.slider-item').length,
		rev = 1;
		
		function checkItems(){
			if(numItems < 4){
				for(var i = 0; i < numItems; i++){
					var thisObj = $(initStrip).find('.slider-item')[i];
					$(thisObj).clone().detach().appendTo($(thisObj).parent()).css({'marginRight': itemMargin + 'px', 'left': '-4px'});
				}
				numItems = $(initStrip).find('.slider-item').length;
				checkItems();
			}else proceed();
		}
		checkItems();
		
		function proceed(){
			var initWidth = (numItems * itemWidth) + (numItems * itemMargin),
			pI = 0,
			animTime = initWidth / settings.speed;
			
			$(initStrip).css({'overflow': 'hidden'});
			$(sliderItems).addClass('initStrip').css({'width': initWidth + 'px'}).wrap('<div id="strip-wrap"></div>');

			var wrap = $(initStrip).parent().find('#strip-wrap'),
			stripClone = $(sliderItems).clone().detach().addClass('stripClone').appendTo($(wrap)).css({'left': initWidth + 'px'});
			
			function scrollStrips(space, timeForAnim){
				$(wrap).animate({'left': '-=' + space}, timeForAnim, "linear", function(){
					var newPos = 0;
					if(pI){
						newPos = parseInt($(sliderItems).css('left')) + initWidth;
						$(stripClone).css({'left': newPos + 'px'});
					}else{
						newPos = parseInt($(stripClone).css('left')) + initWidth;
						$(sliderItems).css({'left': newPos + 'px'});
					}
					pI = !pI;
					rev++;
					scrollStrips(initWidth, animTime);
				});
			}
			scrollStrips(initWidth, animTime);
			
			$(wrap).hover(function(){
				$(this).stop();
			},
			function(){
				var wrapLeft = Math.abs(parseInt($(wrap).css('left'))),
				residualSpace = (initWidth * rev) - wrapLeft,
				timeRemaining = residualSpace / settings.speed;
				scrollStrips(residualSpace, timeRemaining);
			});
		}
	});
};

$.fn.mpColorize = function(phpLoc){
	$(this).find('.colorInput').each(function(){
		var input = $(this);
		var fieldSet = $(input).parent();
		var originalColor = $(input).val();
		var picker = $('<div id="mpPicker"><div id="selector"><div id="selectorBg" style="background-color: ' + originalColor + ';"></div></div><div id="pickerHolder"></div></div>');
		var selector = $(picker).find('#selector');
		var selectorBg = $(picker).find('#selectorBg');
		var holder = $(picker).find('#pickerHolder');
		$(holder).ColorPicker({
			flat: true,
			color: originalColor,
			onSubmit: function(hsb, hex, rgb, el) {
				$(selectorBg).css('backgroundColor', '#' + hex);
				$(input).val('#' + hex);
				$(holder).slideUp();
				widt = !widt;
			},
			onChange: function(hsb, hex, rgb){
				$(selectorBg).css('backgroundColor', '#' + hex);
				$(input).val('#' + hex);
			}
		});
		var widt = false;
		$(holder).find('.colorpicker_current_color').click(function(){
			var hex = $(holder).find('.colorpicker_hex input').val();
			$(selectorBg).css('backgroundColor', '#' + hex);
			$(input).val('#' + hex);
		});
		$(selector).click(function(){
			if(!widt) $(holder).slideDown();
			else $(holder).slideUp();
			widt = !widt;
		});
		$(input).after($(picker));
	});
	$(this).mpUpdate(phpLoc);
};

