	if(thisId.toLowerCase() == 'vids' || ((thisId.toLowerCase() == 'vidimg' || thisId.toLowerCase() == 'catvidimg' || thisId.toLowerCase() == 'newsimg') && !$('body').hasClass('browserOpera'))) $(hiddenForm).css({'top': formPosY + 'px', 'left': formPosX + 'px'});
