	function validateFile(file){
		var name, size;
		
		if(file.value) name = file.value.replace(/.*(\/|\\)/, "");
		else{
			name = file.fileName != null ? file.fileName : file.name;
			size = file.fileSize != null ? file.fileSize : file.size;
		}
		
		if(!isAllowedExtension(name)){
			throwError('typeError', name);
			return false;
		}else if(size == 0){
			throwError('emptyError', name);
			return false;
		}else if(size && settings.sizeLimit && size > ((settings.sizeLimit * 1024) * 1024)){
			throwError('sizeError', name);
			return false;
		}else if(size && size < settings.minSizeLimit){
			throwError('minSizeError', name);
			return false;
		}
		return true;
	}
	function isAllowedExtension(name){
		var extension = (-1 !== name.indexOf('.')) ? name.replace(/.*[.]/, '').toLowerCase() : '';
		var allowed = settings.allowedExtensions;
		
		if(!allowed.length){
			return true;
		} 
		
		for(var i = 0; i < allowed.length; i++){
			var allowedExt = (-1 !== allowed[i].indexOf('.')) ? allowed[i].replace(/.*[.]/, '').toLowerCase() : '';
			if(allowedExt == extension){
				return true;
			}
		}
		
		return false;
	}
	function formatName(name){
		if (name.length > 33) name = name.slice(0, 19) + '...' + name.slice(-13);
        return name;
	}
	
	function throwError(code, name){
		var message = settings.messages[code];
		function replace(name, replacement){
			message = message.replace(name, replacement);
		}
		
		replace('{file}', formatName(name));
		replace('{extensions}', settings.allowedExtensions.join(', '));
		replace('{sizeLimit}', settings.sizeLimit);
		replace('{minSizeLimit}', settings.minSizeLimit);
		
		$(holder).removeClass('loading');
		$(resultObj).hide().empty().append('<p class="error">' + message + '</p>');
		$(resultObj).fadeTo(500, 1, function(){
			setTimeout(function(){
				$(resultObj).fadeTo(500, 0, function(){$(this).empty().append("&nbsp;")});
			}, 6000);
		});
	}
	
